package org.com.doctorservice.mapper;import lombok.experimental.UtilityClass;import org.com.doctorservice.dto.ScheduleResponseDTO;import org.com.doctorservice.exception.EmptyScheduleException;import org.com.doctorservice.messages.DoctorServiceMessages;import org.com.doctorservice.model.Schedule;import java.util.List;@UtilityClasspublic class ScheduleMapper {    public static List<ScheduleResponseDTO> mapListOfSchedulesToResponseDTO(List<Schedule> schedules) {        return schedules.stream()                .map(schedule -> ScheduleResponseDTO.builder()                        .scheduleStartTime(schedule.getStartTime().toString())                        .scheduleEndTime(schedule.getEndTime().toString())                        .scheduleDate(schedule.getScheduleDate().toString())                        .breakStartTime(schedule.getBreakStartTime().toString())                        .breakEndTime(schedule.getBreakEndTime().toString())                        .dayOfWeek(schedule.getCustomDayOfTheWeek().toString())                        .isDayOff(String.valueOf(schedule.isDayOff()))                        .build())                .toList();    }    public static ScheduleResponseDTO mapScheduleToResponseDTO(Schedule schedule) {        ScheduleResponseDTO response = ScheduleResponseDTO.builder()                .scheduleStartTime(schedule.getStartTime().toString())                .scheduleEndTime(schedule.getEndTime().toString())                .scheduleDate(schedule.getScheduleDate().toString())                .breakStartTime(schedule.getBreakStartTime().toString())                .breakEndTime(schedule.getBreakEndTime().toString())                .dayOfWeek(schedule.getCustomDayOfTheWeek().toString())                .isDayOff(String.valueOf(schedule.isDayOff()))                .build();        return response;    }}